let currentDate=new Date;const monthNames=["January","February","March","April","May","June","July","August","September","October","November","December"];let month=currentDate.getMonth(),year=currentDate.getFullYear();function htmlCalendar(e,t){let r=document.querySelector(".calendar-year"),n=document.querySelector(".calendar-month");r.textContent=t,n.textContent=monthNames[e];let o=document.querySelector(".calendar").querySelectorAll("td"),l=new Date(t,e,1),a=l.getDate()-l.getDay()+1,c=0;for(;c<35;c++)l.setFullYear(t,e,a++),o[c].textContent=l.getDate(),o[c].style.color=getColor(l,e);if(l.setDate(a),e===l.getMonth()){let r=document.createElement("tr");for(r.innerHTML="<tr><td></td><td></td><td></td><td></td><td></td><td></td><td></td></tr>",document.querySelector(".calendar").querySelector("table").append(r),o=document.querySelector(".calendar").querySelectorAll("td");c<42;c++)l.setFullYear(t,e,a++),o[c].textContent=l.getDate(),o[c].style.color=getColor(l,e)}else 7===document.querySelector(".calendar").querySelector("table").rows.length&&document.querySelector(".calendar").querySelector("table").deleteRow(6)}function getColor(e,t){return 0===e.getDay()||6===e.getDay()?"red":t!=e.getMonth()?"blue":"black"}htmlCalendar(month,year),document.querySelector(".previous-month").addEventListener("click",(()=>{month-=1,month<0&&(month=11,year-=1),htmlCalendar(month,year)})),document.querySelector(".next-month").addEventListener("click",(()=>{month+=1,month>11&&(month=0,year+=1),htmlCalendar(month,year)}));